        ============================
      `,
        "font-weight:bold;font-family:monospace;"
      )
      return recApply(d)(arg1(d))
    }
    return String(arg1)
  }
}

export function template(head, ...rest) {
  if (typeof head === "string") return () => head
  if (Array.isArray(head)) {
    return d =>
      interleave(head, rest.map(recApply(d)))
        .join("")
        .trim()
  }
  return head
}
