import React, {useState} from "react";
import 'react-phone-number-input/style.css'
import PhoneInput from 'react-phone-number-input'
import { Button, Form, Alert } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useUserAuth } from "../context/UserAuthContext";
import { sendTronDeployTrc721SignedTransaction } from "@tatumio/tatum";

const PhoneSignUp = () => {

    const [number, setNumber] = useState("");
    const [otp, setOtp] =useState("");
    const [error, setError] = useState("");
    const [flag, setFlag] = useState(false);
    const [confirmObj, setConfirmObj] = useState("");
    const { setUpRecaptcha } = useUserAuth();
    
    
    const getOtp = async (e) => {
        e.preventDefault();
        setError("");
        if (number === "" || number === undefined )
            return setError("Please enter a valid Phone Number");
        try{
            const response = await setUpRecaptcha(number);
            console.log(response);
            setConfirmObj(response);
            setFlag
        }catch(err){
            setError(err.message);
        }
        
        console.log(number);
    }

    const verifyOtp = (e) =>{
        e.preventDefault();
    };

    return(

        <>
        <div className="p-4 box">
          <h2 className="mb-3">Firebase Auth Phone</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={getOtp} style={{display: !flag ? "block" : "none"}}>
            <Form.Group className="mb-3" controlId="formBasicPhoneNumber">
              <PhoneInput
                defaultCountry="PH"
                value={number}
                onChange={setNumber}
                placeholder="Enter Phone Number"
              />

            <div id="recaptcha-container" />
            </Form.Group>

            <div className="button-right">
                <Link to="/">
                      <Button variant ="secondary">Cancel</Button>
                </Link>
                <Button variant ="primary" type="submit">Send OTP</Button>
            </div>
        </Form>

        <Form onSubmit={verifyOtp} style={{display: !flag ? "block" : "none"}}>
            <Form.Group className="mb-3" controlId="formBasicotp">
                <Form.Control>
                    type="text",
                    placeholder="Enter otp",
                    onChange ={" "}
                    {(e)=>sendTronDeployTrc721SignedTransaction(e.target.value)}
                </Form.Control>

            
            </Form.Group>

            <div className="button-right">
                <Link to="/">
                      <Button variant ="secondary">Cancel</Button>
                </Link>
                <Button variant ="primary" type="submit">Verify OTP</Button>
            </div>
        </Form>
      </div>
        </> 
    );

};

export default PhoneSignUp;
