import React, { useState } from "react";
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router";
import { useUserAuth } from "../context/UserAuthContext";

const Home = () => {
  const { logOut, user } = useUserAuth();
  const navigate = useNavigate();
  const [flag, setFlag]=useState(false);
  const handleLogout = async () => {
    try {
      await logOut();
      navigate("/");
    } catch (error) {
      console.log(error.message);
    }
  };

  const createAccount= async() =>{

    const query = new URLSearchParams({mnemonic: 'string'}).toString();

    const resp = await fetch(
      `https://api-eu1.tatum.io/v3/ethereum/wallet?${query}`,
      {
        method: 'GET',
        headers: {
          'x-testnet-type': 'ethereum-ropsten',
          'x-api-key': '3b9b4784-cfd2-414b-9093-d8ff07b1461e'
        }
      }
    );

    const data = await resp.text();
    console.log(data);
    setFlag(true);
  }
  
  return (
    <>
      <div className="p-4 box mt-3 text-center">
        Welcome to NU10 Home <br />
        {user && user.email}
      </div>
      <div className="d-grid gap-2">
        <Button variant="primary" onClick={handleLogout}>
          Log out
        </Button>
      </div>
      &nbsp;
      <div className="d-grid gap2">

        <Button variant= "secondary" onClick={createAccount}>
              Create wallet
        </Button>

      </div>

      &nbsp;
      <div className="d-grid gap2" style={{display: flag ? "block" : "none"}}>

        <Button variant= "secondary" onClick={createAccount}>
              Get Account Address
        </Button>

      </div>
    </>
  );
};

export default Home;
