export const resolve = pipe([
  resolveCadence,
  resolveArguments,
  resolveAccounts,
  /* special */ execFetchRef,
  /* special */ execFetchSequenceNumber,
  resolveSignatures,
  resolveFinalNormalization,
  resolveValidators,
