import {pipe, put, Ok, makeTransaction} from "../interaction/interaction.js"
import {template} from "@onflow/util-template"

const DEFAULT_COMPUTE_LIMIT = 10
const DEFAULT_SCRIPT_ACCOUNTS = []
const DEFUALT_REF = null

export function transaction(...args) {
  return pipe([
    makeTransaction,
    put("ix.cadence", template(...args)),
    ix => {
      ix.message.computeLimit = ix.message.computeLimit || DEFAULT_COMPUTE_LIMIT
      ix.message.refBlock = ix.message.refBlock || DEFUALT_REF
      ix.authorizations = ix.authorizations || DEFAULT_SCRIPT_ACCOUNTS
      return Ok(ix)
    },
  ])
}
import { Ok, Bad } from "../interaction/interaction.js"

export function invariant(...args) {
  if (args.length > 1) {
    const [predicate, message] = args
    return invariant((ix, { Ok, Bad }) => {
      return predicate ? Ok(ix) : Bad(ix, message)
    })
  }
  const [fn] = args
  return ix => fn(ix, {Ok, Bad})
}
import {isTransaction, Ok} from "../interaction/interaction.js"
import {send} from "../send/sdk-send.js"
import {decodeResponse} from "../decode/decode.js"
import {getAccount} from "../build/build-get-account.js"
import {build} from "../build/build.js"

export const resolveProposerSequenceNumber = ({ node }) => async (ix) => {
  if (!(isTransaction(ix))) return Ok(ix)
  if (ix.accounts[ix.proposer].sequenceNum) return Ok(ix)

  const response = await send(await build([
    getAccount(ix.accounts[ix.proposer].addr)
  ]), { node })
  const decoded = await decodeResponse(response)

  ix.accounts[ix.proposer].sequenceNum = decoded.keys[ix.accounts[ix.proposer].keyId].sequenceNumber

  return Ok(ix)
}
